mapper.select:
/* springsecurity-mapper.xml userIdCheck 사용자 중복확인 */
SELECT COUNT(1)
  FROM tbsy_user tb1
 WHERE tb1.user_id = #{user_id}

mapper.insert:
/* springsecurity-mapper.xml insertLogin 사용자 로그인 정보 입력 */
INSERT
  INTO tbsy_login
	 ( user_id
	 , user_pw
	 , role_id
	 )
VALUES
	 ( #{user_id}
	 , #{user_pw}
	 , 'USER'
	 )

/* springsecurity-mapper.xml insertUser 사용자 입력 */
INSERT
  INTO tbsy_user
	 ( user_id
	 , user_name
	 )
VALUES
	 ( #{user_id}
	 , #{user_name}
	 )
